import {
  setVal,
  clearValue,
  getValores,
  currentOps,
  setOps,
} from "../actions/calc/calcSlice";
import { useDispatch, useSelector } from "react-redux";
import { resultValuex } from "../utils";

const Keyboard = () => {
  const dispatch = useDispatch();
  const valores = useSelector(getValores);
  const current = useSelector(currentOps);
  const buttons = ["0", "1", "AC", "="];

  const buttonAction = (val) => {
    handleActions(val);
    switch (val) {
      case "AC":
        dispatch(clearValue());
        break;
      case "=":
        dispatch(resultValuex(valores));
        break;

      default:
        if (current === "result") {
          dispatch(clearValue(""));
          dispatch(setOps(""));
          dispatch(setVal(val));
        } else {
          dispatch(setVal(val));
        }
    }
  };

  return (
    <>
      <div className="flex  flex-wrap p-2 gap-3 justify-center">
        {buttons.map((r) => {
          return (
            <div
              key={r}
              className={`rounded-3xl w-[45%] h-[100px] text-center shadow-xl bg-white  ${
                r === "AC" || r === "="
                  ? "bg-purple-600 hover:bg-purple-500"
                  : "hover:bg-blue-100"
              }`}
            >
              {/* <button
                className={`h-[100%] w-[100%] text-3x ${
                  r === "AC" ? "bg-red-100" : "bg-green-100"
                }`}
              >
                {r}
              </button> */}
              <button
                className={`h-[100%] w-[100%] text-3x ${
                  r === "AC" || r === "=" ? "text-white" : "text-black"
                }`}
                onClick={() => buttonAction(r)}
              >
                {r}
              </button>
            </div>
          );
        })}
      </div>
    </>
  );
};

export default Keyboard;
