import { useEffect, useState } from "react";
import { useSelector } from "react-redux";

export const Display = ({ action }) => {
  const calcNumber = useSelector((s) => s.calc.value);

  const [value, setValue] = useState(0);
  const changeValue = (e) => {
    setValue(e.target.value);
  };

  useEffect(() => {
    console.log(action);
  }, [action]);
  return (
    <div className="h-[126px] flex justify-end items-end border-b-2 border-white p-2 bg-black flex-col overflow-auto">
      <span className="text-white text-sm">2400</span>
      <input
        className="text-white bg-black text-end w-[100%] text-3xl"
        readOnly
        value={calcNumber}
      ></input>
    </div>
  );
};

export default Display;
