import { setMath } from "../actions/calc/calcSlice";

export const resultValuex = (valores) => (dispatch) => {
  let binArr = valores.split("");
  let decArr = [];
  console.log(binArr);

  for (let i = 0; i < binArr.length; i++) {
    let dec = 2 ** i;
    decArr.push(dec);
  }

  let decVal = 0;

  decArr.forEach((element, index) => {
    decVal += binArr[index] === "0" ? 0 : 5;
    console.log(decVal);

    // decVal += binArr[index] !== "0" ? element : 0;
    // console.log(decVal);
  });

  console.log(decVal);
  console.log(decArr.reverse());
};
