import { setOps, setResult, clearValue } from "../actions/calc/calcSlice";

export const resultValuex = (valores) => (dispatch) => {
  let binArr = valores.split("");
  let decArr = [];
  console.log(binArr);

  for (let i = 0; i < binArr.length; i++) {
    let dec = 2 ** i;
    decArr.push(dec);
  }

  let decVal = 0;

  decArr.reverse().forEach((element, index) => {
    console.log(element);
    decVal += binArr[index] === "0" ? 0 : element;
  });

  dispatch(setResult(decVal.toString()));
  dispatch(setOps("result"));
  console.log(decArr.reverse());
};

export const handleActions = (val) => (dispatch) => {
  switch (val) {
    case "AC":
      dispatch(clearValue());
      break;
    case "=":
      dispatch(resultValue(valores));
      break;

    default:
      if (current === "result") {
        dispatch(clearValue(""));
        dispatch(setOps(""));
        dispatch(setVal(val));
      } else {
        dispatch(setVal(val));
      }
  }
};
