import {
  setOps,
  setResult,
  clearValue,
  getValores,
  setVal,
} from "../actions/calc/calcSlice";
import { useSelector } from "react-redux";

export const resultValue = (valores) => (dispatch) => {
  let binArr = valores.split("");
  let decArr = [];

  for (let i = 0; i < binArr.length; i++) {
    let dec = 2 ** i;
    decArr.push(dec);
  }

  let decVal = 0;

  decArr.reverse().forEach((element, index) => {
    decVal += binArr[index] === "0" ? 0 : element;
  });

  dispatch(setResult(decVal.toString()));
  dispatch(setOps("result"));
};

export const handleActions = (val) => (dispatch) => {
  switch (val) {
    case "AC":
      dispatch(clearValue());
      break;
    case "=":
      console.log();
      dispatch(resultValue(val));
      break;
    default:
      dispatch(setVal(val));
    //   if (current === "result") {
    //     dispatch(clearValue(""));
    //     dispatch(setOps(""));
    //     dispatch(setVal(val));
    //   } else {
    //     dispatch(setVal(val));
    //   }
  }
};
